import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [pokemons, setPokemons] = useState([]);
  const [newPokemon, setNewPokemon] = useState({ name: '', type: '', imageUrl: '' });

  // Carrega os Pokémon do servidor
  useEffect(() => {
    axios.get('http://localhost:5000/pokemons')
      .then((response) => setPokemons(response.data))
      .catch((error) => console.error('Erro ao carregar os Pokémon:', error));
  }, [pokemons]);

  // Função para adicionar um novo Pokémon
  const handleAddPokemon = () => {
    if (newPokemon.name && newPokemon.type && newPokemon.imageUrl) {
      axios.post('http://localhost:5000/pokemons', newPokemon)
        .then((response) => {
          setPokemons([...pokemons, response.data]);
          setNewPokemon({ name: '', type: '', imageUrl: '' });
        })
        .catch((error) => console.error('Erro ao adicionar Pokémon:', error));
    }
  };

  return (
    <div className="App">
      <h1>Pokémon Manager</h1>

      {/* Formulário para adicionar Pokémon */}
      <div>
        <input
          type="text"
          placeholder="Nome"
          value={newPokemon.name}
          onChange={(e) => setNewPokemon({ ...newPokemon, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Tipo"
          value={newPokemon.type}
          onChange={(e) => setNewPokemon({ ...newPokemon, type: e.target.value })}
        />
        <input
          type="text"
          placeholder="URL da Imagem"
          value={newPokemon.imageUrl}
          onChange={(e) => setNewPokemon({ ...newPokemon, imageUrl: e.target.value })}
        />
        <button onClick={handleAddPokemon}>Adicionar Pokémon</button>
      </div>

      {/* Exibindo os Pokémon */}
      <div>
        {pokemons.map((pokemon, index) => (
          <div key={index} className="pokemon-card">
            <img src={pokemon.imageUrl} alt={pokemon.name} />
            <h2>{pokemon.name}</h2>
            <p>{pokemon.type}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
